//Componentes
import { Spinner } from 'react-bootstrap';
//Axios usage
import axios from 'axios';
//use Effect needed
import { useEffect, useState } from 'react';

const API_URL = process.env.REACT_APP_API_URL;

function Phones() {
	//Coming from GET
	const [ infoPhone, setInfoPhone ] = useState([]);
	const [ isLoading, setIsLoading ] = useState(false);

	const getInfoPhone = () => {
		// Get the token from the localStorage
		const storedToken = localStorage.getItem('authToken');

		// Send the token through the request "Authorization" Headers
		const { id } = params.id;

		console.log(id);

		console.log('hello');

		axios
			.get(`${API_URL}/telefonos/:phoneID`, { headers: { Authorization: `Bearer ${storedToken}` } })
			.then((response) => {
				setInfoPhone(response.data);
				//console.log(response.data);
				setIsLoading(true);
			})
			.catch((error) => console.log(error));
	};

	// We set this effect will run only once, after the initial render
	// by setting the empty dependency array - []
	useEffect(() => {
		getInfoPhone();
	}, []);

	return (
		<div>
			<h1>Phone Page</h1>
			<div className="carousel__Div">
				{infoPhone.map((phone) => (
					<div className="PhoneDiv__Container" key={phone.idPhone}>
						<div className="card-deck">
							<div className="card marginBottom">
								<img className="card-img-top image-Phone" src={phone.imageFileName} alt="Phone" />
								<div className="card-body">
									<h3 className="card-title">
										{phone.name} - {phone.manufacturer}
									</h3>
									<p className="card-text noMargin infoTextPhone">{phone.description}</p>
									<p className="card-text noMargin">Color: {phone.color}</p>
									<p className="card-text noMargin">Screen: {phone.screen}</p>
									<p className="card-text noMargin">Processor: {phone.processor}</p>
									<p className="card-text noMargin">Ram: {phone.ram}</p>
									<p className="card-text noMargin">ID: {phone._id}</p>
									<p className="card-text noMargin boldText">
										Price: <span class="greenText">{phone.price}€</span>
									</p>
								</div>
							</div>
						</div>
					</div>
				))}
			</div>
		</div>
	);
}

export default Phones;
