import { Spinner } from 'react-bootstrap';
//Axios usage
import axios from 'axios';
//use Effect needed
import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const API_URL = process.env.REACT_APP_API_URL;

function Phones() {
	//Coming from GET
	const [ infoPhone, setInfoPhone ] = useState([]);
	//const [ isLoading, setIsLoading ] = useState(false);
	const { id } = useParams();

	const getInfoPhone = () => {
		// Get the token from the localStorage
		const storedToken = localStorage.getItem('authToken');

		// console.log('hello');
		// console.log(id);

		axios
			.get(`${API_URL}/telefonos/${id}`, { headers: { Authorization: `Bearer ${storedToken}` } })
			.then((response) => {
				setInfoPhone(response.data);
				console.log(response.data);
				//setIsLoading(true);
			})
			.catch((error) => console.log(error));
	};

	// We set this effect will run only once, after the initial render
	// by setting the empty dependency array - []
	useEffect(() => {
		getInfoPhone();
	}, []);

	return (
		<div>
			<h1>Phone Page Details</h1>
			<div className="carousel__Div">
				<div className="PhoneDiv__Container" key={infoPhone.idPhone}>
					<div className="card-deck">
						<div className="card marginBottom">
							{/* <img className="card-img-top image-Phone" src={infoPhone.imageFileName} alt="Phone" /> */}
							<div className="card-body">
								{/* <h3 className="card-title">
									{infoPhone.name} - {infoPhone.manufacturer}
